import React, { useState, useEffect } from 'react';

const CurrencyExchange = () => {
    const [exchangeRates, setExchangeRates] = useState([]);
    const [pair, setPair] = useState('');
    const [rate, setRate] = useState('');

    useEffect(() => {
        // Fetch exchange rates from the backend API
        fetch('/api/exchange-rates/')
            .then(response => response.json())
            .then(data => setExchangeRates(data));
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Save the new exchange rate to the backend API
        fetch('/api/exchange-rates/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ pair, rate }),
        })
            .then(response => response.json())
            .then(data => {
                setExchangeRates([...exchangeRates, data]);
                setPair('');
                setRate('');
            });
    };

    return (
        <div className="container">
            <h1 className="my-4">Currency Exchange Rates</h1>
            <form onSubmit={handleSubmit} className="mb-4">
                <div className="form-group">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Currency Pair (e.g., USD-EUR)"
                        value={pair}
                        onChange={(e) => setPair(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <input
                        type="text"
                        className="form-control"
                        placeholder="Rate"
                        value={rate}
                        onChange={(e) => setRate(e.target.value)}
                        required
                    />
                </div>
                <button type="submit" className="btn btn-primary">Add Rate</button>
            </form>
            <h2 className="my-4">Exchange Rates</h2>
            <table className="table table-striped">
                <thead>
                    <tr>
                        <th>Pair</th>
                        <th>Rate</th>
                        <th>Last Updated</th>
                    </tr>
                </thead>
                <tbody>
                    {exchangeRates.map((rate, index) => (
                        <tr key={index}>
                            <td>{rate.pair}</td>
                            <td>{rate.rate}</td>
                            <td>{new Date(rate.updated_at).toLocaleString()}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default CurrencyExchange;
